<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN" "http://www.w3.org/TR/html4/loose.dtd"><html><head><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"><title>Browse Files</title></head><body><?$current_dir = "/home/uploads/";$dir = opendir($current_dir);echo "Upload directory is $current_dir<br>";echo "Directory Listing:<br><hr><br>";while ($file = readdir($dir))	{			echo "$file<br>";	}echo "<hr><br>";closedir($dir);?><p>&nbsp;</p></body></html>