<table width="80%"  cellpadding="5" cellspacing="0" border="0" align="left"><tr>	<td class="cattitle" align="left" valign="middle" colspan="2"><?php echo $this->qvalues['operation'] ; ?></td></tr><tr>	<td class="cattitle" align="left" valign="middle">Question Name</td>	<td class="cattitle" align="left" valign="middle"><input type="text" name="ques_name" value="<?php echo $this->qvalues['qname'];?>"></td></tr><tr>	<td class="cattitle" align="left" valign="middle">Question Stem</td>	<td class="cattitle" align="left" valign="middle"><textarea name="ques_stem" rows="5" cols="40"><?php echo $this->qvalues['stem']; ?></textarea></td></tr><tr>	<td class="cattitle" align="left" valign="middle">Answer</td>	<td class="cattitle" align="left" valign="middle">		<select name="ques_ans">		<?php 			$tf = array('FALSE', 'TRUE'); 					for ($k=1 ;$k>= 0 ; $k--) {				if ( $this->qvalues['answer'] == $k ) {					echo '<option value="'.$k.'" selected>'.$tf[$k].'</option>';				} else {								echo '<option value="'.$k.'">'.$tf[$k].'</option>';				}			}		?>		</select>	</td></tr><tr>	<td class="cattitle" align="center" valign="middle"><input type="submit" value="<?php echo $this->qvalues['button_label']; ?>"></td>	<td class="cattitle" align="center" valign="middle"><input type="Reset"></td>	</tr><input type="hidden" name="action" value="<?php echo $this->qvalues['oper'] ; ?>"><input type="hidden" name="qtype" value="2"><input type="hidden" name="cid" value="<?php echo $this->cid ; ?>"><input type="hidden" name="qid" value="<?php echo $this->qid ; ?>"><input type="hidden" name="quesid" value="<?php echo $this->quesid ; ?>"></table>